'use strict';

var node_fs = require('node:fs');
var promises = require('node:fs/promises');
var os = require('node:os');
var node_path = require('node:path');

exports.Project = void 0;
(function (Project) {
    Project["SEMAPHORE_IDENTITY"] = "semaphore-identity";
    Project["POSEIDON"] = "poseidon";
    // RLN = 'rln',
    Project["SEMAPHORE"] = "semaphore";
})(exports.Project || (exports.Project = {}));
const projects = Object.values(exports.Project);

async function getAvailableVersions(project) {
    const res = await fetch(`https://registry.npmjs.org/@zk-kit/${project}-artifacts`);
    const { versions } = await res.json();
    return Object.keys(versions);
}
async function isVersionAvailableOrThrow(project, version) {
    const availableVersions = await getAvailableVersions(project);
    if (version !== 'latest' && !availableVersions.includes(version))
        throw new Error(`Version '${version}' is not available for project '${project}'`);
}
async function getBaseUrls(project, version) {
    await isVersionAvailableOrThrow(project, version);
    return [
        `https://unpkg.com/@zk-kit/${project}-artifacts@${version}/${project}`,
        `https://raw.githubusercontent.com/privacy-scaling-explorations/snark-artifacts/@zk-kit/${project}-artifacts@${version}/packages/${project}/${project}`,
        `https://cdn.jsdelivr.net/npm/@zk-kit/${project}-artifacts@${version}/${project}`,
    ];
}
async function getSnarkArtifactUrls(project, options = {}) {
    if (!projects.includes(project))
        throw new Error(`Project '${project}' is not supported`);
    options.version ??= 'latest';
    const urls = await getBaseUrls(project, options.version);
    const parameters = options.parameters
        ? `-${options.parameters.join('-')}`
        : '';
    return {
        wasms: urls.map((url) => `${url}${parameters}.wasm`),
        zkeys: urls.map(url => `${url}${parameters}.zkey`),
    };
}

async function fetchRetry(urls) {
    const [url] = urls;
    if (!url)
        throw new Error('No urls to try');
    return fetch(url).catch(() => fetchRetry(urls.slice(1)));
}
async function download(urls, outputPath) {
    const { body, ok, statusText, url } = await fetchRetry(urls);
    if (!ok)
        throw new Error(`Failed to fetch ${url}: ${statusText}`);
    if (!body)
        throw new Error('Failed to get response body');
    const dir = node_path.dirname(outputPath);
    await promises.mkdir(dir, { recursive: true });
    const fileStream = node_fs.createWriteStream(outputPath);
    const reader = body.getReader();
    try {
        const pump = async () => {
            const { done, value } = await reader.read();
            if (done) {
                fileStream.end();
                return;
            }
            fileStream.write(Buffer.from(value));
            await pump();
        };
        await pump();
    }
    catch (error) {
        fileStream.close();
        throw error;
    }
}
// https://unpkg.com/@zk-kit/poseidon-artifacts@latest/poseidon.wasm -> @zk/poseidon-artifacts@latest/poseidon.wasm
const extractEndPath = (url) => url.substring(url.indexOf('@zk'));
async function maybeDownload(urls) {
    const outputPath = `${os.tmpdir()}/${extractEndPath(urls[0])}`;
    if (!node_fs.existsSync(outputPath))
        await download(urls, outputPath);
    return outputPath;
}
/**
 * Downloads SNARK artifacts (`wasm` and `zkey`) files if not already present in OS tmp folder.
 * @example
 * ```ts
 * {
 *   wasm: "/tmp/@zk-kit/semaphore-artifacts@latest/semaphore-3.wasm",
 *   zkey: "/tmp/@zk-kit/semaphore-artifacts@latest/semaphore-3.zkey" .
 * }
 * ```
 * @returns {@link SnarkArtifacts}
 */
async function maybeGetSnarkArtifacts(...pars) {
    const { wasms, zkeys } = await getSnarkArtifactUrls(...pars);
    const [wasm, zkey] = await Promise.all([
        maybeDownload(wasms),
        maybeDownload(zkeys),
    ]);
    return {
        wasm,
        zkey,
    };
}

exports.getAvailableVersions = getAvailableVersions;
exports.maybeGetSnarkArtifacts = maybeGetSnarkArtifacts;
exports.projects = projects;
